(2515, 2, '2015-06-02 09:52:31', '2015-06-02 09:52:31', '<div class=\"padre theme-clear-fix\"><figure class=\"fecha\">\r\n<div class=\"circulo\"></div>\r\n<div class=\"desglose\">\r\n<h4>VI Edición</h4>\r\nDel 15 de Junio al 30 de Septiembre\r\n\r\nPlazas limitadas\r\n\r\n</div>\r\n</figure><figure class=\"donde\">\r\n<div class=\"circulo\"></div>\r\n<div class=\"desglose\">\r\n<h4>Duración</h4>\r\n100 horas\r\n\r\nTeleformación\r\n\r\n</div>\r\n</figure><figure class=\"precio\">\r\n<div class=\"circulo\"></div>\r\n<div class=\"desglose\">\r\n<h4>Precio</h4>\r\n750€\r\n\r\n(10% descuento por pago único)\r\n\r\n100% bonificable\r\n\r\n</div>\r\n</figure><figure class=\"matricularse\">\r\n<div class=\"circulo\"><!--\r\n<div class=\"hoja-check\"></div>\r\n--></div>\r\n<div class=\"desglose\">\r\n<h4><a href=\"http://cursomediacioncivilymercantil.com/wp-content/uploads/matriculese.pdf\" target=\"_blank\">Matricúlese</a></h4>\r\nDescargue <a href=\"http://cursomediacioncivilymercantil.com/wp-content/uploads/matriculese.pdf\" target=\"_blank\">aquí</a> el documento de matrícula\r\ny envíelo a\r\n<a href=\"mailto:info@fundaciondidactica.org\" target=\"_blank\">info@fundaciondidactica.org</a>\r\n\r\n</div>\r\n</figure></div>', 'datos', '', 'inherit', 'open', 'open', '', '2481-revision-v1', '', '', '2015-06-02 09:52:31', '2015-06-02 09:52:31', '', 2481, 'http://cursoauxiliargeriatria.mainfor.edu.es/2481-revision-v1/', 0, 'revision', '', 0),
(2516, 2, '2015-06-02 09:54:03', '2015-06-02 09:54:03', '<div class=\"caja-publi\">\r\n<h3>Oportunidad laboral</h3>\r\n<ul>\r\n	<li>Inscríbase en el <a href=\"http://www.mjusticia.gob.es/cs/Satellite/Portal/es/servicios-ciudadano/tramites-gestiones-personales/registro-mediadores\" target=\"_blank\">Registro de Mediadores</a></li>\r\n	<li>Ofrezca a sus clientes servicios de mediación y amplíe su oferta profesional</li>\r\n	<li>Forme parte de los listados de mediadores de cámaras de comercio, colegios profesionales, asociaciones, y otras instituciones de mediación</li>\r\n	<li>Externalice sus servicios profesionales dentro de la UE a través de la mediación telemática</li>\r\n	<li>Acceda a más de 10 ofertas diarias en el ámbito de la mediación a nivel nacional</li>\r\n	<li>Resuelva conflictos de índole laboral en su empresa de forma satisfactoria</li>\r\n	<li>Adquiera habilidades y técnicas de comunicación y aplíquelas en cualquier ámbito profesional y personal</li>\r\n</ul>\r\n</div>', 'oportunidad', '', 'inherit', 'open', 'open', '', '2479-revision-v1', '', '', '2015-06-02 09:54:03', '2015-06-02 09:54:03', '', 2479, 'http://cursoauxiliargeriatria.mainfor.edu.es/2479-revision-v1/', 0, 'revision', '', 0),
